var previousScroll=0,menuOffset=60,detachPoint=900,hideShowOffset=6;$(window).scroll(function(){if(!$("#nav").hasClass("expanded")){var b=$(this).scrollTop(),a=Math.abs(b-previousScroll);if(b>menuOffset){if(b>detachPoint){if(!$("#nav").hasClass("detached")){$("#nav").addClass("detached")}}if(a>=hideShowOffset){if(b>previousScroll){if(!$("#nav").hasClass("invisible")){$("#nav").addClass("invisible")}}else{if($("#nav").hasClass("invisible")){$("#nav").removeClass("invisible")}}}}else{if(b<=0){$("#nav").removeClass()}}if((window.innerHeight+window.scrollY)>=document.body.offsetHeight){$("#nav").removeClass("invisible")}previousScroll=b}});$("#nav").on("click touchstart",function(a){showHideNav();a.preventDefault()});$("#navigation").on("click touchstart",function(a){a.stopPropagation()});function showHideNav(){if($("#nav").hasClass("expanded")){hideNav()}else{showNav()}}function showNav(){$("#nav").removeClass("invisible").addClass("expanded");$("#container").addClass("blurred");window.setTimeout(function(){$("body").addClass("no_scroll")},200);$("#navigation a").attr("tabindex","")}function hideNav(){$("#container").removeClass("blurred");window.setTimeout(function(){$("body").removeClass("no_scroll")},10);$("#nav").removeClass("expanded");$("#navigation a").attr("tabindex","-1");$(".icon").blur()}$("body").keydown(function(a){if($("nav .icon").is(":focus")){if(a.keyCode===13||a.keyCode===32){showHideNav();a.preventDefault()}}if(a.keyCode===27||a.keyCode===77){showHideNav();a.preventDefault()}});